package Loop;

public class Evennu {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int n=100;
		 int i=1;  
	        System.out.print("even numbers 1 to 100");  
	        while (i<=n)   
	        { 
	            if ( i%2 == 0 )
	            {
	                System.out.println(i + " ");
	            }
	            i++;
	        }

	}

}
