package conditions;

public class switchcas {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int D=500;
		switch(D) {
		case 200:
			System.out.println(D);
			break;
		case 300:
			System.out.println(D);
			break;
		case 500:
			System.out.println("the value of case3 is  "+D);
			break;
		default:
				System.out.println("Value of default is "+D);
			
		}
		

	}

}
