package operators;

public class Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=2;
		int n2=2;
		n1=n2;
		System.out.println("output of n1=n2 is "+n1);
		n1+=n2;//n1=n1+n2
		System.out.println("output of  n1+=n2 is "+n1);
        n1++;//n1+1
        System.out.println(n1);
	}

}
